import React, { useState } from "react";
import axios from "axios";
import CardsList from "./card";
import Input from "./input";

import "./App.css";
const App = () => {
  const [movies, setMovies] = useState({
    query: "",
    result: [],
  });

  const handle = (e) => {
    let s = e.target.value;

    setMovies((prevState) => {
      return { ...prevState, query: s };
    });
  };
  /*   const apiKey = "fac34116c639f3e75cd5205251be421c";*/
  const api = `https://api.themoviedb.org/3/search/movie?api_key=fac34116c639f3e75cd5205251be421c`;

  const search = (e) => {
    if (e.key === "Enter") {
      axios(api + `&query=` + movies.query).then(({ data }) => {
        let result = data.results;

        setMovies((prevState) => {
          return { ...prevState, result: result };
        });
      });
    }
  };

  /* useEffect(() => {
    fetch(api)
      .then((data) => data.json())
      .then((data) => setMovies(data.results));
  }, [api]);
 */
  return (
    <div>
      <Input handle={handle} search={search} />
      <div className="row">
        <CardsList result={result} />
        {console.log(movies)}
      </div>
    </div>
  );
};

export default App;
