import React, { useState } from "react";
import { MDBBtn, MDBCollapse } from "mdbreact";
import { Button } from "react-bootstrap";
import Trailer from "./trailer";

const CollapsePage = (props) => {
  const [collapse, setCollapse] = useState({
    collapseID: "",
  });

  const toggleCollapse = (collapseID) => (e) => {
    e.preventDefault();
    setCollapse((prevState) => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : "",
    }));
  };

  return (
    <>
      <MDBBtn color="dark" href="#" onClick={toggleCollapse("basicCollapse")}>
        Read More
      </MDBBtn>
      <MDBCollapse id="basicCollapse" isOpen={collapse.collapseID}>
        <div>
          <p className="overview">{props.overview}</p>
          <p className="release">
            <p className="rating">Ratings: {props.rating}</p>
            <p className="date">Date: {props.date}</p>
          </p>
          <p className="language">Language: {props.language}</p>
        </div>
        <Button className="seetrailerbtn">See Trailer</Button>
      </MDBCollapse>
    </>
  );
};

export default CollapsePage;
