import React, { useState, useEffect } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import axios from "axios";
import Trailer from "./trailer";
import { IoIosStarOutline } from "react-icons/io";

const Cards = ({ result }) => {
  const [showPopup, setShowPopup] = useState(false);
  const [trailerKey, setTrailerKey] = useState([]);
  const [cast, setCast] = useState([]);
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const trailer = `http://api.themoviedb.org/3/movie/${result.id}/videos?api_key=${apiKey}&append_to_respone=videos`;
  const credit = `https://api.themoviedb.org/3/movie/${result.id}/credits?api_key=fac34116c639f3e75cd5205251be421c`;
  const Open = () => setShowPopup(true);
  const Close = () => setShowPopup(false);

  useEffect(() => {
    axios(trailer).then(({ data }) => {
      setTrailerKey(data.results);
      console.log(data.results);
    });
  }, [trailer]);

  let key = "";
  for (let i = 0; i < trailerKey.length; i++) {
    let name = trailerKey[i].type;
    if (name === "trailer") {
      key = trailerKey[i].key;
    }
  }
  /* let key = "";
       for (let i = 0; i < trailerKey.length; i++) {
      let name = trailerKey[i].name.toLowerCase().split(" "); 
      for (let j = 0; j < name.length; j++) {
          if (name[j] === "official" && name[j + 1] === "trailer") {
              key = trailerKey[i].key;
              name = trailerKey[i].name;
              break loop;
          }
          if (name[j] === "trailer") {
              key = trailerKey[i].key;
              name = trailerKey[i].name;
              break loop;
          }
      }
  } */

  useEffect(() => {
    axios(credit).then(({ data }) => {
      /* setCast(data); */
      /* console.log(data); */
    });
  }, [credit]);

  return (
    <div className="card">
      <Card style={{ width: "15rem" }} onClick={Open}>
        <button>
          <Card.Img
            variant="top"
            src={`http://image.tmdb.org/t/p/w185/` + result.poster_path}
            alt="no image found"
          />
        </button>
      </Card>

      <Modal show={showPopup} onHide={Close} centered size="lg">
        <div className="popup">
          <Modal.Header>
            <Modal.Title>
              <div className="titlemovie">
                {result.original_title}({result.release_date.substring(0, 4)})
              </div>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div className="discription">
              <img
                className="modalimg"
                src={"http://image.tmdb.org/t/p/w185/" + result.poster_path}
                alt="ok"
              />
              {console.log(key)}
              <div className="text">
                <div className="data">
                  <p>
                    <IoIosStarOutline style={{ color: "yellow" }} />
                    <IoIosStarOutline style={{ color: "yellow" }} />
                    <IoIosStarOutline style={{ color: "yellow" }} />
                    {result.vote_average}
                  </p>
                  <p>language={result.original_language}</p>
                </div>
                <h5> {result.overview}</h5>
              </div>
            </div>
            <Trailer trailer={key} />
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={Close}>
              Close
            </Button>
          </Modal.Footer>
        </div>
      </Modal>
    </div>
  );
};
export default Cards;
