import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import apiKey from "./apikey";
/* import Popup from "./popup"; */

const Cards = ({ result }) => {
  const [showPopup, setShowPopup] = useState(false);
  const trailer = `http://api.themoviedb.org/3/movie/38700/videos?api_key=${apiKey}&append_to_respone=videos`;

  const Open = () => setShowPopup(true);
  const Close = () => setShowPopup(false);

  return (
    <div className="card">
      <Card style={{ width: "15rem" }} onClick={Open}>
        <button>
          <Card.Img
            variant="top"
            src={`http://image.tmdb.org/t/p/w185/` + result.poster_path}
            alt="no image found"
          />
        </button>
      </Card>
      <div className="popup">
        <Modal show={showPopup} onHide={Close} centered size="lg">
          <Modal.Header>
            <Modal.Title>
              {result.original_title}({result.release_date.substring(0, 4)})
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>{result.id}</p>
            <h5> {result.overview}</h5>
            <p>rating={result.vote_average}</p>
            <p>language={result.original_language}</p>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={Close}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </div>
  );
};
export default Cards;
