import React, { useState, useEffect } from "react";
import axios from "axios";
import CardsList from "./card";
import Input from "./input";
import "../app.css";
import Latest from "./latest";

const App = () => {
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&page=1&include_adult=false&query=avengers`;
  /*   const latestmovies = `https://api.themoviedb.org/3/movie/now_playing?api_key=${apiKey}`;
   */ const [now, setNow] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [movies, setMovies] = useState({
    query: "",
    result: [],
  });

  useEffect(() => {
    axios(api).then(({ data }) => {
      let result = data.results;
      setMovies((prevState) => {
        return { ...prevState, result: result };
      });
    });
  }, [api]);

  const click = (event) => {
    let ls = event.target.value;
    axios(api).then(({ data }) => {
      setMovies((prevState) => {
        return { ...prevState, query: ls };
      });
      console.log({ query: ls });
    });
    console.log(ls);
  };

  /* const handle = (e) => {
