import React, { useState, useEffect } from "react";
import axios from "axios";
import CardsList from "./card";
import Input from "./input";
import Yess from "./latestmovies";

import "../app.css";
import gsap from "gsap";
const App = () => {
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}`;
  const latest = `https://api.themoviedb.org/3/movie/now_playing?api_key=${apiKey}&language=en-US&page=1`;
  const [show, setShow] = useState(true);
  const [latestMovies, setLatestMovies] = useState([]);
  const [movies, setMovies] = useState({
    query: "",
    result: [],
  });

  const handle = (e) => {
    let s = e.target.value;
    setMovies((prevState) => {
      return { ...prevState, query: s };
    });
  };

  useEffect(
    (e) => {
      axios(latest).then(({ data }) => {
        setLatestMovies(data.results);
      });
      console.log(e);
    },
    [latest]
  );

  const search = (e) => {
    axios(api + `&query=` + movies.query).then(({ data }) => {
      let result = data.results;
      setMovies((prevState) => {
        return { ...prevState, result: result };
      });
    });
    e.preventDefault();
    if (e === search) {
      gsap.to(".latest", 1, { display: "none" });
    }
  };

  return (
    <div>
      <div className="header">
        <div
          className="row"
          style={{
            justifyContent: "space-around",
            alignItems: "center",
            width: "100%",
          }}
        >
          <h1>Quarantine Movies</h1>
          <Input handle={handle} search={search} />
        </div>
        {console.log(latestMovies)}
      </div>
      <div className="latestM">
        <h3>latest Movies</h3>
        <div className="row">
          {latestMovies.map((latestM) => (
            <Yess key={latestM.id} img={latestM.poster_path} />
          ))}
        </div>
      </div>
      <div className="">
        <CardsList key={movies.id} results={movies.result} />
        {console.log(movies)}
      </div>
    </div>
  );
};

export default App;
