import React, { useState, useEffect } from "react";
import axios from "axios";
import CardsList from "./card";
import Input from "./input";
import "../app.css";
import Latest from "./latest";

const App = () => {
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}`;
  const latestmovies = `https://api.themoviedb.org/3/movie/now_playing?api_key=${apiKey}&language=en-US&page=1`;
  const [now, setNow] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [movies, setMovies] = useState({
    query: "",
    result: [],
  });

  const handle = (e) => {
    let s = e.target.value;
    setMovies((prevState) => {
      return { ...prevState, query: s };
    });
  };

  const search = (e) => {
    axios(api + `&query=` + movies.query).then(({ data }) => {
      let result = data.results;
      setMovies((prevState) => {
        return { ...prevState, result: result };
      });
    });
    e.preventDefault();
  };

  useEffect(() => {
    axios(latestmovies).then(({ data }) => {
      console.log(data);
      setNow(data.results);
    });
  }, [latestmovies]);

  const fetchNewPage = () => {
    axios(latestmovies).then(({ data }) => {
      console.log(currentPage + 1);
      setNow(data.page);
      setCurrentPage(data.page);
    });
  };

  const load = () => {
    const newPage = `https://api.themoviedb.org/3/movie/now_playing?api_key=${apiKey}&language=en-US&page=${currentPage +
      1}`;
    console.log(newPage);
    fetchNewPage(newPage);
  };

  return (
    <div>
      <div className="header">
        <div
          className="row"
          style={{
            justifyContent: "space-around",
            alignItems: "center",
            width: "100%",
          }}
        >
          <h1>Quarantine Movies</h1>
          <Input handle={handle} search={search} />
        </div>
      </div>
      <h3 style={{ color: "white", marginLeft: "25px" }}>Latest Movies</h3>
      <div className="row" style={{ justifyContent: "space-evenly" }}>
        {now.map((now) => (
          <Latest
            key={now.id}
            id={now.id}
            img={now.poster_path}
            title={now.original_title}
            original_language={now.original_language}
            vote_average={now.vote_average}
            overview={now.overview}
            release_date={now.release_date}
          />
        ))}
      </div>
      <button onClick={load}> load more</button>
      {console.log(now)}
      {console.log(movies)}
      <div className="">
        <CardsList key={movies.id} results={movies.result} />
        {console.log(movies)}
      </div>
    </div>
  );
};

export default App;
