import React, { useState, useEffect } from "react";
import CardsList from "./card";
import Input from "./input";

import "./App.css";
const App = () => {
  const [movies, setMovies] = useState([]);
  const [input, setInput] = useState("");
  const [submit, setSubmit] = useState("");
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const search = `${submit}`;
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${search}`;

  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((res) => setMovies(res.results));
  }, []);

  function Change(event) {
    console.log(event.target.value);
    setInput(event.target.value);
  }
  function Click(e) {
    e.preventDefault();
    setSubmit(input);
  }

  return (
    <div>
      <div className="row">
        {movies.map((movie) => (
          <CardsList
            key={movie.id}
            id={movie.id}
            overview={movie.overview}
            img={movie.poster_path}
          />
        ))}
        <input onChange={Change} type="text" name="movie" value={input}></input>
        <button onClick={Click} type="submit">
          search
        </button>
        {console.log(movies)}
      </div>
    </div>
  );
};

export default App;
