import React, { useState, useEffect } from "react";
import CardsList from "./card";
import Input from "./input";

import "./App.css";
const App = (props) => {
  const [movies, setMovie] = useState([]);
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const search = <Input search={props.search} />;
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${search}`;

  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((res) => setMovie(res.results));
  }, [api]);

  return (
    <div>
      <Input />
      <div className="row">
        {movies.map((movie) => (
          <CardsList
            key={movie.id}
            id={movie.id}
            overview={movie.overview}
            img={movie.poster_path}
          />
        ))}
        {console.log(movies)}
      </div>
    </div>
  );
};

export default App;
