import React from "react";
import Card from "react-bootstrap/Card";
import CollapsePage from "./readmore";

const Cards = ({ result }) => {
  return (
    <div className="card">
      <Card style={{ width: "18rem" }}>
        <Card.Img
          variant="top"
          src={`http://image.tmdb.org/t/p/w185/` + result.poster_path}
        />
        <Card.Title>
          <p>{result.overview.substring(0, 50)}</p>
        </Card.Title>
        <CollapsePage overview={result.overview} />
      </Card>
    </div>
  );
};
export default Cards;
