import React, { useState } from "react";
import axios from "axios";
import CardsList from "./card";
import Input from "./input";
import Popup from "./popup";
/* import Trailer from "./trailer"; */

import "./App.css";
const App = (props) => {
  /*  const [movieId, setMovieId] = useState(38700); */
  const [movies, setMovies] = useState({
    query: "",
    result: [],
    selected: {},
  });
  /* const { trailers, setTrailers } = useState([]); */

  const handle = (e) => {
    let s = e.target.value;
    setMovies((prevState) => {
      return { ...prevState, query: s };
    });
  };
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}`;
  /*   const trailer = `http://api.themoviedb.org/3/movie/38700/videos?api_key=${apiKey}&append_to_respone=videos`;
   */
  /* axios(trailer).then(({ data }) => {
    let ok = data.results;
    console.log(ok);
  }); */

  const search = (e) => {
    /* e.preventDefault(); */
    if (e.key === "Enter") {
      axios(api + `&query=` + movies.query).then(({ data }) => {
        let result = data.results;

        setMovies((prevState) => {
          return { ...prevState, result: result };
        });
      });

      e.preventDefault();
    }
  };

  const openPopup = (id) => {
    axios(api + `&i=` + id).then(({ data }) => {
      let result = data;

      setMovies((prevState) => {
        return { ...prevState, selected: result };
      });
    });
  };

  const closePopup = () => {
    setMovies((prevState) => {
      return { ...prevState, selected: {} };
    });
  };

  return (
    <div>
      <h1>Quarantine Movies</h1>
      <Input handle={handle} search={search} />
      <div className="">
        <CardsList
          key={movies.id}
          results={movies.result}
          openPopup={openPopup}
        />
        {console.log(movies)}
      </div>
      {typeof movies.selected.original_title != "undefinded" ? (
        <Popup selected={movies.selected} closePopup={closePopup} />
      ) : (
        false
      )}
    </div>
  );
};

export default App;
