import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
/* import Popup from "./popup"; */

const Cards = ({ result }) => {
  const [showPopup, setShowPopup] = useState(false);

  const Open = () => setShowPopup(true);
  const Close = () => setShowPopup(false);

  return (
    <div className="card">
      <Card style={{ width: "15rem" }} onClick={Open}>
        <button>
          <Card.Img
            variant="top"
            src={`http://image.tmdb.org/t/p/w185/` + result.poster_path}
            alt="no image found"
          />
        </button>
      </Card>
      <Modal show={showPopup} onHide={Close}>
        <Modal.Header closeButton>
          <Modal.Title>{result.original_title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Modal.Text>{result.overview}</Modal.Text>
          rating={result.vote_average}
          {result.overview}
          date={result.release_date}
          language={result.original_language}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={Close}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};
export default Cards;
