import React from "react";
const Input = ({ handle, search }) => {
  return (
    <form>
      <input
        onChange={handle}
        type="text"
        name="movie"
        placeholder="Search for a Movie..."
        /*  onKeyPress={search} */
      />
      <button type="submit" onClick={search}>
        Search
      </button>
    </form>
  );
};

export default Input;
