import React, { useState } from "react";

const Input = (props) => {
  const [input, setInput] = useState("");
  const [search, setSearch] = useState("");

  function Change(event) {
    console.log(event.target.value);
    setInput(event.target.value);
  }
  function Click() {
    setSearch(input);
  }

  return (
    <form>
      <h1>{search}</h1>
      <input
        onKeyPress={(event) => {
          if (event.apiKey === "search") {
            props.event(document.getElementById("root").value);
          }
        }}
        type="text"
        placeholder="search"
      />
      <button onClick={Click} search={props.search}>
        Search
      </button>
      {/*  {console.log(input)} */}
    </form>
  );
};

export default Input;
