import React, { useState, useEffect } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import axios from "axios";
import Trailer from "./trailer";

const Cards = ({ result }) => {
  const [showPopup, setShowPopup] = useState(false);
  const [trailerKey, setTrailerKey] = useState([]);
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const trailer = `http://api.themoviedb.org/3/movie/${result.id}/videos?api_key=${apiKey}&append_to_respone=videos`;
  const genre = `https://api.themoviedb.org/3/${result.genre_ids}/movie/list?api_key=${apiKey}&language=en-US`;

  const Open = () => setShowPopup(true);
  const Close = () => setShowPopup(false);

  useEffect(() => {
    axios(trailer).then(({ data }) => {
      setTrailerKey(data.results[0].key);
      console.log(data.results);
    });
  }, [trailer]);

  useEffect(() => {
    axios(genre).then(({ data }) => {
      console.log(data);
    });
  }, [genre]);

  return (
    <div className="card">
      <Card style={{ width: "15rem" }} onClick={Open}>
        <button>
          <Card.Img
            variant="top"
            src={`http://image.tmdb.org/t/p/w185/` + result.poster_path}
            alt="no image found"
          />
        </button>
      </Card>

      <Modal show={showPopup} onHide={Close} centered size="lg">
        <div className="popup">
          <Modal.Header>
            <Modal.Title>
              <div className="titlemovie">
                {result.original_title}({result.release_date.substring(0, 4)})
              </div>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div className="discription">
              <img
                className="modalimg"
                src={"http://image.tmdb.org/t/p/w185/" + result.poster_path}
                alt="ok"
              />
              <div className="text">
                <div className="data">
                  <p>rating={result.vote_average}</p>
                  <p>language={result.original_language}</p>
                </div>
                <h5> {result.overview}</h5>
              </div>
            </div>
            <Trailer trailer={trailerKey} />
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={Close}>
              Close
            </Button>
          </Modal.Footer>
        </div>
      </Modal>
    </div>
  );
};
export default Cards;
