import React, { useState, useEffect } from "react";
import CardsList from "./test";
import Input from "./input";
import "./App.css";
/* import Create from "./test";
 */ const App = () => {
  const [movies, setMovie] = useState([]);
  const [text, setText] = useState([]);
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const search = "how";
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${search}`;

  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((res) => setMovie(res.results));
  }, [api]);

  function click() {
    setText(movies.map((e) => e.overview[1]));

    console.log(text);
  }

  return (
    <div>
      <Input />
      <div className="row">
        {movies.map((movie) => (
          <CardsList
            click={click}
            key={movie.id}
            id={movie.id}
            overview={movie.overview.substring(0, 50)}
            img={movie.poster_path}
          />
        ))}
        {/*   {console.log(movies)} */}
      </div>
    </div>
  );
};

export default App;
