import React, { useState, useEffect } from "react";
import CardsList from "./card";
import Input from "./input";

import "./App.css";
const App = () => {
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&query=${movies.s}`;

  const [movies, setMovies] = useState({
    s: "",
    result: [],
  });

  const handle = (e) => {
    let s = e.target.value;

    setMovies((prevState) => {
      return { ...prevState, s: s };
    });
    console.log(movies.s);
  };

  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((res) => setMovies(res.results));
  }, [api]);

  return (
    <div>
      <Input handle={handle} />
      <div className="row">
        {/* {movies.map((movie) => (
          <CardsList
            key={movie.id}
            id={movie.id}
            overview={movie.overview}
            img={movie.poster_path}
          />
        ))} */}
        {console.log(movies)}
      </div>
    </div>
  );
};

export default App;
