import React from "react";
import gsap from "gsap";

const Input = (props) => {
  const click = (prosp) => {
    console.log(props.search);
  };
  return (
    <form>
      <input
        onChange={props.handle}
        type="text"
        name="movie"
        placeholder="Search for a Movie..."
      />
      <button className="search" type="submit" onClick={props.search}>
        Search
      </button>
    </form>
  );
};

export default Input;
