import React from "react";
import gsap from "gsap";

const Input = (props) => {
  const change = (e) => {
    if (e === props.search) {
      gsap.to(".latestM", 1, { display: "block" });
    }
  };

  return (
    <form>
      <input
        onChange={props.handle}
        type="text"
        name="movie"
        placeholder="Search for a Movie..."
      />
      <button
        className="search"
        onChange={change}
        type="submit"
        onClick={props.search}
      >
        Search
      </button>
    </form>
  );
};

export default Input;
