import React, { useState, useEffect } from "react";
import axios from "axios";
import CardsList from "./card";
import Input from "./input";
import "../app.css";
import Latest from "./latest";

const App = () => {
  const apiKey = "fac34116c639f3e75cd5205251be421c";
  const api = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&page=1&include_adult=false&query=avengers`;
  const [currentPage, setCurrentPage] = useState(1);
  const [movies, setMovies] = useState({
    query: "",
    result: [],
  });

  /*  useEffect(() => {
    const newPage = api;
  }, [api]); */
  useEffect(() => {
    const newPage = api;
    fetchNewPage(newPage);

    axios(newPage).then(({ data }) => {
      let result = data.results;
      setMovies((prevState) => {
        return { ...prevState, result: result };
      });
    });
  }, [api]);

  const handle = (e) => {
    let s = e.target.value;
    setMovies((prevState) => {
      return { ...prevState, query: s };
    });
  };

  const changeMovies = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&page=1&include_adult=false`;

  const search = (e) => {
    axios(changeMovies + `&query=` + movies.query).then(({ data }) => {
      let result = data.results;
      setMovies((prevState) => {
        return { ...prevState, result: result };
      });
    });
    e.preventDefault();
  };

  const fetchNewPage = (path) => {
    axios(path).then(({ data }) => {
      console.log(data);
      setCurrentPage(data.page);
    });
  };

  const load = () => {
    const newPage = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&include_adult=false&query=avengers&page=${currentPage +
      1}`;
    fetchNewPage(newPage);
  };

  return (
    <div>
      <div className="header">
        <div
          className="row"
          style={{
            justifyContent: "space-around",
            alignItems: "center",
            width: "100%",
          }}
        >
          <h1>Quarantine Movies</h1>
          <Input handle={handle} search={search} />
        </div>
      </div>
      <h3 style={{ color: "white", marginLeft: "25px" }}>Latest Movies</h3>
      {console.log(movies)}
      <div className="">
        <CardsList key={movies.id} results={movies.result} />
        {console.log(movies)}
      </div>
      <button onClick={load}> load more</button>
    </div>
  );
};

export default App;
