import React, { useState, useEffect } from "react";
import CardsList from "./test";
import "./App.css";
/* import Create from "./test";
 */ const App = () => {
  const [movies, setMovie] = useState([]);
  const api =
    "https://api.themoviedb.org/3/search/movie?api_key=fac34116c639f3e75cd5205251be421c&query=search";
  const test = movies.map((movie) => movie.overview.substring(0, 30));
  console.log(test);
  useEffect(() => {
    fetch(api)
      .then((res) => res.json())
      .then((res) => setMovie(res.results));
  }, []);

  return (
    <div className="row">
      {movies.map((movie) => (
        <CardsList
          key={movie.id}
          title={movie.title}
          overview={movie.overview.substring(0, 40)}
          img={movie.poster_path}
        />
      ))}
      {console.log(movies)}
    </div>
  );
};

export default App;
