import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Trailer from "./trailer";

const Latest = (props) => {
  const [showPopup, setShowPopup] = useState(false);
  const Open = () => setShowPopup(true);
  const Close = () => setShowPopup(false);
  return (
    <div>
      <Card className="movie" style={{ width: "13rem" }} onClick={Open}>
        <button>
          <Card.Img
            variant="top"
            src={`https://image.tmdb.org/t/p/w185/` + props.img}
            alt="no image found"
          />
        </button>
      </Card>
      <Modal show={showPopup} onHide={Close} centered size="lg">
        <div className="popup">
          <Modal.Header>
            <Modal.Title>
              <div className="titlemovie">
                {props.title}
                {/* ({props.release_date.substring(0, 4)}) */}
              </div>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div className="container-fluid">
              <div className="discription">
                <div className="row">
                  <div className="modalimg">
                    <img
                      src={"https://image.tmdb.org/t/p/w185/" + props.img}
                      alt="ok"
                    />
                  </div>
                  <div className="text">
                    <div className="data">
                      <p>
                        {/*  <IoIosStarOutline style={{ color: "yellow" }} />
                        <IoIosStarOutline style={{ color: "yellow" }} />
                        <IoIosStarOutline style={{ color: "yellow" }} /> */}
                        {props.vote_average}
                      </p>
                      <p>
                        {/*   <MdLanguage className="earth" /> */}
                        {props.original_language}
                      </p>
                    </div>
                    <h5> {props.overview}</h5>
                  </div>
                </div>
              </div>
            </div>
            <Trailer /* trailer={trailerMovie} */ />
            <div className="actors">
              <div className="row" style={{ justifyContent: "space-evenly" }}>
                {/*  {.map(() => ({
                   <Actors
                    key={cast.id}
                    name={cast.name}
                    character={cast.character}
                    img={cast.profile_path}
                  /> 
                }))}  */}
              </div>
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={Close}>
              Close
            </Button>
          </Modal.Footer>
        </div>
      </Modal>
    </div>
  );
};

export default Latest;
