import React, { useState, useEffect } from "react";
import axios from "axios";
import CardsList from "./card";
import Input from "./input";

import "./App.css";
const App = () => {
  const [movies, setMovies] = useState({
    query: "",
    result: [],
  });

  const handle = (e) => {
    let s = e.target.value;

    setMovies((prevState) => {
      return { ...prevState, query: s };
    });
  };
  /*   const apiKey = "fac34116c639f3e75cd5205251be421c";*/
  const api = `https://api.themoviedb.org/3/search/movie?api_key=fac34116c639f3e75cd5205251be421c`;

  const search = (e) => {
    if (e.key === "Enter") {
      axios(api + `&query=` + movies.query).then((data) => {
        console.log(data.results);
      });
    }
  };

  /* useEffect(() => {
    fetch(api)
      .then((data) => data.json())
      .then((data) => setMovies(data.results));
  }, [api]);
 */
  return (
    <div>
      <Input handle={handle} search={search} />
      <div className="row">
        {/* {movies.map((movie) => (
          <CardsList
            key={movie.id}
            id={movie.id}
            overview={movie.overview}
            img={movie.poster_path}
          />
        ))} */}
        {console.log(movies)}
      </div>
    </div>
  );
};

export default App;
